mapper.select:
select attach_no attachNo, src_type srcType, src_no srcNo, src_seq srcSeq, in_url inUrl, ex_url exUrl, upload_usr uploadUsr, modified, created
from HS_ATTACH_DB
where src_type = #{srcType} 

and src_no = #{srcNo}


and upload_usr = #{uploadUsr}

select attach_no attachNo, src_type srcType, src_no srcNo, src_seq srcSeq, in_url inUrl, ex_url exUrl, upload_usr uploadUsr, modified, created
from HS_ATTACH_DB
where ex_url = #{exUrl}
