package com.house.model;import java.sql.Timestamp;import com.house.util.DateUtils;public class SudatalkCategory {		private int cateNo;	private int depth;	private String cateNm;	private String modified;	private String created;	public int getCateNo() {		return cateNo;	}	public int getDepth() {		return depth;	}	public String getCateNm() {		return cateNm;	}	public String getModified() {		return modified;	}	public String getCreated() {		return created;	}	public void setCateNo(int cateNo) {		this.cateNo = cateNo;	}	public void setDepth(int depth) {		this.depth = depth;	}	public void setCateNm(String cateNm) {		this.cateNm = cateNm;	}	public void setModified(String modified) {		this.modified = modified;	}	public void setCreated(String created) {		this.created = created;	}		public void setModifiedByDate(Timestamp timestamp) {		this.modified = DateUtils.dateToString(timestamp, DateUtils.dateForm1);	}	public void setCreatedByDate(Timestamp timestamp) {		this.created = DateUtils.dateToString(timestamp, DateUtils.dateForm1);	}}